INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (3, 'Elfen', 'Mittelgroß', 'Elfen sind mittelgroße Kreaturen und erhalten keinerlei Boni oder Mali aufgrund ihrer Größe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (4, 'Elfen', 'Dämmersicht', 'Elfen können bei schlechter Beleuchtung doppelt so weit sehen wie Menschen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (5, 'Elfen', 'Geschärfte Sinne', 'Elfen erhalten einen Volksbonus von +2 auf Fertigkeitswürfe für Wahrnehmung.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (6, 'Elfen', 'Elfische Immunität', 'Elfen sind immun gegen magische Schlaf-Effekte und erhalten einen Volksbonus von +2 auf Rettungswürfe gegen Zauber oder magische Effekte aus der Schule der Verzauberung.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (7, 'Elfen', 'Elfenmagie', 'Elfen erhalten einen Volksbonus von +2 auf Zauberstufenwürfe, wenn es um die Überwindung von Zauberresistenz geht. Darüber hinaus erhalten sie einen Volksbonus von +2 auf Fertigkeitswürfe für Zauberkunde, die der Ermittlung der Eigenschaften magischer Gegenstände dienen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (8, 'Elfen', 'Waffenvertrautheit', 'Elfen sind geübt im Umgang mit Langbögen (einschließlich Kompositlangbögen), und behandeln jede Waffe, die als „elfisch“ bezeichnet wird, als Kriegswaffe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (9, 'Gnome', 'Attribute', '+2 KO, +2 CH, -2 ST');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (10, 'Gnome', 'Klein', 'Gnome sind kleine Kreaturen, so dass sie einen Größenbonus von +1 auf ihre RK, einen Größenbonus von +1 auf Angriffswürfe und einen Größenbonus von +4 auf Fertigkeitswürfe für Heimlichkeit erhalten, allerdings erhalten sie einen Malus von -1 auf ihren KMB und ihre KMV.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (11, 'Gnome', 'Geringe Bewegungsrate', 'Gnome haben eine Grundbewegungsrate von 6 m.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (12, 'Gnome', 'Dämmersicht', 'Gnome können bei schlechter Beleuchtung doppelt so weit sehen wie Menschen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (13, 'Gnome', 'Geschärfte Sinne', 'Gnome erhalten einen Volksbonus von +2 auf Fertigkeitswürfe für Wahrnehmung.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (14, 'Gnome', 'Eifrig', 'Gnome erhalten einen Volksbonus von +2 auf eine Handwerks- oder Berufsfertigkeit deiner Wahl.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (15, 'Gnome', 'Illusionsresistenz', 'Gnome erhalten einen Volksbonus von +2 auf Rettungswürfe gegen Illusionseffekte.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (16, 'Gnome', 'Gnomenmagie', 'Gnome erhöhen den SG aller Rettungswürfe gegen von ihnen gewirkte Illusionszauber um +1. Gnome mit einem Charisma von 11 oder höher erhalten außerdem die folgenden zauberähnlichen Fähigkeiten: 1/Tag - Geisterhaftes Geräusch, Mit Tieren Sprechen, Tanzende Lichter, Zaubertrick. Die Zauberstufe für diese Effekte entspricht der Charakterstufe des Gnoms. Der SG für Rettungswürfe gegen diese Zauber ist gleich 10 + Grad des Zaubers + dem Charisma-Modifikator des Gnoms.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (17, 'Gnome', 'Waffenvertrautheit', 'Gnome behandeln jede Waffe, die als „gnomisch“ bezeichnet wird, als Kriegswaffe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (18, 'Gnome', 'Hass', 'Aufgrund einer speziellen Ausbildung erhalten Gnome einen Bonus von +1 auf Angriffswürfe gegen humanoide Kreaturen, die den Unterarten Reptil oder Goblinoider angehören.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (19, 'Gnome', 'Verteidigungsschulung', 'Gegen Angriffe von Monstern der Kreaturenart Riese erhalten Gnome einen Ausweichbonus von +4 auf ihre RK.');
