INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (25, 'Halb-Elfen', 'Elfische Immunität', 'Halb-Elfen sind immun gegen magische Schlaf-Effekte und erhalten einen Volksbonus von +2 auf Rettungswürfe gegen Zauber oder magische Effekte aus der Schule der Verzauberung.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (26, 'Halb-Elfen', 'Anpassungsfähig', 'Als Bonustalent erhalten Halb-Elfen auf der 1. Stufe das Talent Fertigkeitsfokus.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (27, 'Halb-Elfen', 'Elfenblut', 'Für jeden auf das Volk bezogenen Effekt werden Halb-Elfen sowohl als Elfen als auch als Menschen behandelt.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (28, 'Halb-Elfen', 'Multitalent', 'Halb-Elfen dürfen sich auf der 1. Stufe zwei bevorzugte Klassen aussuchen und erhalten entweder +1 Trefferpunkte oder +1 Fertigkeitspunkte, wenn sie in einer dieser Klassen eine Stufe aufsteigen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (29, 'Halb-Orks', 'Attribute', '+2 auf einen Attributswert');
