INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (31, 'Halb-Orks', 'Normale Bewegunsrate', 'Halb-Orks haben eine Grundbewegungsrate von 9 m.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (32, 'Halb-Orks', 'Dunkelsicht', 'Halb-Orks können im Dunkeln bis zu 18 m weit sehen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (33, 'Halb-Orks', 'Waffenvertrautheit', 'Halb-Orks sind geübt im Umgang mit dem Krummschwert und der Zweihändigen Axt und behandeln alle Waffen, die als „orkisch“ bezeichnet werden, als Kriegswaffen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (34, 'Halb-Orks', 'Orkische Wildheit', 'Ein Mal pro Tag, wenn ein Halb-Ork auf weniger als 0 Trefferpunkte reduziert wurde, ohne dabei zu Tode zu kommen, kann er für eine Runde weiterkämpfen, als ob er nur kampfunfähig wäre. Sollte er dann nicht wieder auf über 0 Trefferpunkte gebracht worden sein, so wird er am Ende seines nächsten Zuges sofort bewusstlos und beginnt zu sterben.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (35, 'Halb-Orks', 'Orkblut', 'Für jeden auf das Volk bezogenen Effekt werden Halb-Orks sowohl als Orks als auch als Menschen behandelt.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (36, 'Halb-Orks', 'Einschüchtern', 'Halb-Orks erhalten einen Volksbonus von +2 auf Würfe für Einschüchtern, da sie eben einen furchterregenden Eindruck machen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (37, 'Halblinge', 'Attribute', '+2 GE, +2 CH, -2 ST');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (38, 'Halblinge', 'Klein', 'Halblinge sind kleine Kreaturen, so dass sie einen Größenbonus von +1 auf ihre RK, einen Größenbonus von +1 auf Angriffswürfe und einen Größenbonus von +4 auf Fertigkeitswürfe für Heimlichkeit erhalten. Allerdings erhalten sie einen Malus von -1 auf ihren KMB und ihre KMV.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (39, 'Halblinge', 'Geringe Bewegungsrate', 'Halblinge haben eine Grundbewegungsrate von 6 m.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (40, 'Halblinge', 'Wendig', 'Halblinge erhalten einen Volksbonus von +2 auf Fertigkeitswürfe für Akrobatik und Klettern.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (41, 'Halblinge', 'Halblingsglück', 'Halblinge erhalten eine Volksbonus von +1 auf alle Rettungswürfe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (42, 'Halblinge', 'Furchtlos', 'Halblinge erhalten einen Volksbonus von +2 auf alle Rettungswürfe gegen Furcht. Dieser Bonus addiert sich mit dem Bonus für Halblingsglück.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (43, 'Halblinge', 'Waffenvertrautheit', 'Halblinge sind geübt im Umgang mit Schleudern und behandeln alle Waffen, die das Wort „Halbling“ im Namen tragen, als Kriegswaffen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (44, 'Menschen', 'Attribute', '+2 auf einen Attributswert');
