INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (46, 'Menschen', 'Normale Bewegungsrate', 'Menschen haben eine Grundbewegungsrate von 9 m.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (47, 'Menschen', 'Bonustalent', 'Menschen wählen ein zusätzliches Talent auf der 1. Stufe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (48, 'Menschen', 'Geschult', 'Menschen erhalten einen zusätzlichen Fertigkeitsrang auf der 1. Stufe und einen weiteren zusätzlichen Fertigkeitsrang bei jedem Stufenaufstieg.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (49, 'Zwerge', 'Attribute', '+2 KO, +2 WE, -2 CH');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (50, 'Zwerge', 'Mittelgroß', 'Zwerge sind mittelgroße Kreaturen und erhalten keinerlei Bonus oder Malus aufgrund ihrer Größe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (51, 'Zwerge', 'Ruhig und sicher', 'Zwerge haben eine Grundbewegungsrate von 6 m, aber ihre Bewegungsrate wird nie durch Belastung oder Rüstung verändert.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (52, 'Zwerge', 'Dunkelsicht', 'Zwerge können im Dunkeln bis zu 18 m weit sehen.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (53, 'Zwerge', 'Steingespür', 'Zwerge erhalten einen Bonus von +2 auf Fertigkeitswürfe für Wahrnehmung, um ungewöhnliches Stein- oder Mauerwerk zu bemerken – wie etwa Fallen oder Geheimtüren, die sich in steinernen Wänden oder Böden befinden. Sie dürfen einen Fertigkeitswurf machen, um solche Eigenarten zu bemerken, wann immer sie diese innerhalb von 3 m Entfernung passieren, unabhängig davon, ob sie bewusst danach suchen oder nicht.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (54, 'Zwerge', 'Gier', 'Zwerge erhalten einen Volksbonus von +2 auf Würfe für Schätzen, um den Preis nicht-magischer Güter zu ermitteln, die wertvolle Metalle oder Edelsteine beinhalten.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (55, 'Zwerge', 'Abgehärtet', 'Zwerge erhalten einen Volksbonus von +2 für Rettungswürfe gegen Gifte, Zauber und zauberähnliche Fähigkeiten.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (56, 'Zwerge', 'Waffenvertrautheit', 'Zwerge sind geübt im Umgang mit Streitäxten, schweren Streithämmern und Kriegshämmern, und sie behandeln jede Waffe, die als „zwergisch“ bezeichnet wird, als eine Kriegswaffe.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (57, 'Zwerge', 'Hass', 'Aufgrund einer speziellen Ausbildung erhalten Zwerge einen Bonus von +1 auf Angriffswürfe gegen humanoide Kreaturen, welche der Ork- oder Goblin-Unterart angehören.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (58, 'Zwerge', 'Verteidigungsschulung', 'Gegen Angriffe von Monstern der Unterart Riese erhalten Zwerge einen Ausweichbonus von +4 auf ihre RK.');
INSERT INTO `mydb`.`Rassenboni` (`idRassenboni`, `Rassen_idRasse`, `Boni`, `Bezeichnung`) VALUES (59, 'Zwerge', 'Standfestigkeit', 'Zwerge erhalten einen Volksbonus von +4 auf ihre Kampfmanöververteidigung, wenn sie einem Ansturm oder einem Versuch widerstehen wollen, sie zu Fall zu bringen, wenn sie auf einem festen Untergrund stehen.');
